mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY A.IBOARD DESC) ROWNUMBER, A.IBOARD, A.ICODE, A.TITLE, (SELECT NAME FROM tbl_code WHERE ICODE = A.GENRE) GENRE, IFNULL(C.NAME, '') TUMBNAIL, IFNULL(A.YOUTUBE_ID, '') YOUTUBE_ID, A.CREATED_AT, A.SEC_YN FROM tbl_board A INNER JOIN tbl_code B ON A.ICODE = B.ICODE LEFT JOIN tbl_thumbnail C ON A.IBOARD = C.IBOARD WHERE A.DEL_YN = 'N' AND A.ICODE = #{code}
AND A.TITLE LIKE CONCAT('%', #{search}, '%')
AND SEC_YN = 'N'
ORDER BY A.IBOARD DESC LIMIT #{offset}, #{amount}
